<?phpuse Illuminate\Pagination\LengthAwarePaginator;function paginate($itemsar, $perPage = 25){//Get current page form url e.g. &page=1    $currentPage = LengthAwarePaginator::resolveCurrentPage();    $items  = collect($itemsar);//Slice the collection to get the items to display in current page    $currentPageItems = $items->slice(($currentPage - 1) * $perPage, $perPage);//Create our paginator and pass it to the view    return new LengthAwarePaginator($currentPageItems, count($items), $perPage);}